import Header from "./components/Header";
import Footer from "./components/Footer";
import React from "react";
import Items from "./components/Items";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      orders: [],
      items: [
        {
          id: 1,
          title: "Стул чёрный",
          img: "chair.jpeg",
          desc: "Loren ispum",
          category: "chairs",
          price: "49.99",
        },
        {
          id: 2,
          title: "Стол",
          img: "table.jpeg",
          desc: "Loren ispum",
          category: "tables",
          price: "149.99",
        },
        {
          id: 3,
          title: "Телевизор",
          img: "tv.webp",
          desc: "Loren ispum",
          category: "tv",
          price: "69.99",
        },
        {
          id: 4,
          title: "Стул чёрный",
          img: "chair.jpeg",
          desc: "Loren ispum",
          category: "chairs",
          price: "49.99",
        },
        {
          id: 5,
          title: "Стол",
          img: "table.jpeg",
          desc: "Loren ispum",
          category: "tables",
          price: "149.99",
        },
        {
          id: 6,
          title: "Телевизор",
          img: "tv.webp",
          desc: "Loren ispum",
          category: "tv",
          price: "69.99",
        },
      ],
    };
    this.addToOrder = this.addToOrder.bind(this);
  }
  render() {
    return (
      <div className="wrapper">
        <Header orders={this.state.orders} />
        <Items items={this.state.items} onAdd={this.addToOrder} />

        <Footer />
      </div>
    );
  }
  addToOrder(item) {
    this.setState({ orders: [...this.state.orders, item] }, () =>
      console.log(this.state.orders)
    );
  }
}

export default App;
